// Function to find the minimum value among a variable number of integers
int min(int n, int m, int k) {
  int r = n; // Initialize result with the first element
  if (m <= r) { // Update result if a smaller element is found
    r = m;
  }
  if (k <= r) { // Update result if a smaller element is found
    r = k;
  }
  return r; // Return the minimum value
}

